import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";

function InputNote(props) {
  const [note, setNote] = useState({ key: "", title: "", content: "" });

  function saveNoteState(event) {
    console.log(event.target.value);
    const newNote = {
      id: uuidv4(),
      title: event.target.value,
      cotent: event.target.value,
    };

    setNote(newNote);
  }
  return (
    <div>
      <form>
        <input name="title" onChange={saveNoteState} placeholder="Title" />
        <textarea
          name="content"
          onChange={saveNoteState}
          placeholder="Take a note..."
          rows="3"
        />
        <button
          onClick={() => {
            props.addNote(note);
            //setNote({ key: "", title: "", content: "" });
          }}
        >
          Add
        </button>
      </form>
    </div>
  );
}

export default InputNote;
